			            <view v-if="city == ''" style="color: #fff;">城市入驻</view>
									<view style="color: #fff;">{{city}}</view>
									<image style="width: 32rpx;
    height: 32rpx;
    margin-left: 6rpx;
    margin-top: 10rpx;" src="../../static/image/weizhiicon.png"></image>
									</view>
			        </pick-regions>
		</view>
	<view class="box">
