			<swiper-item v-for="(item,index) in banner" :key = 'index'>
				<image :src="item.images" mode="widthFix"></image>
			</swiper-item>
		</swiper>
		<view class="iconlist">
			<view class="iconitem" @tap="tourl(weiruzhu)" v-if="rz != 1">
				<image :src="weiruzhu.url"></image>
