			</view>
			<view class="iconitem" @tap="tourl(ruzhu)" v-if="rz == 1">
				<image :src="ruzhu.url"></image>
				<view>{{ruzhu.title}}</view>
			</view>
			<view class="iconitem" v-for="(item,index) in iconlist" :key="index" @tap="tourl(item)">
				<image :src="item.url"></image>
				<view>{{item.title}}</view>
			</view>
		</view>
		<view class="newswiper">
			<image class="biaoicon" src="../../static/image/nswleft.png"></image>
			<swiper class="newswiperright" :autoplay="true" :circular="true">
				<swiper-item v-for="(item,index) in gonggao" :key="index" @tap="ggdet(item)">
					<!-- <view class="lable">{{item.label}}</view> -->
					<view class="nsbiao">{{item.title}}</view>
				</swiper-item>
			</swiper>
		</view>
		<view class="newbox">
			<view class="netl">
			<view class="newtitle">资讯要闻</view>
			<view class="crimson"></view>
			</view>
