<script>
	import pickRegions from '@/components/pick-regions/pick-regions.vue'
	export default{
		 components:{ 
			 pickRegions
			 },
		data(){
			return{
				page:1,
				limit:100,
				rz:'',
				xian:false,
				user_id:'',
				city2:uni.getStorageSync('userInfo').city,
				city:'',
				banner:[],
				ruzhu:{title:"提交事迹",tourl:'./tijiaoshiji',url:'data:image/png;base64,'},
				weiruzhu:{title:"我要入驻",tourl:'./woyaoruzhu',url:'data:image/png;base64,'},
				iconlist:[
					{title:"家风驿站",tourl:'./jiafengyizhan',url:'data:image/png;base64,'},
					{title:"评分标准",tourl:'./pingfenbiaozhun',url:'data:image/png;base64,'},
					{title:"活动剪影",tourl:'./huodongjianying',url:'data:image/png;base64,'}
				],
				newlist:[],
				gonggao:[]
			}
		},
		onLoad() {
			let _this = this;
			// if(!uni.getStorageSync('userInfo')){
			// 	this.xian = !this.xian
			// }
			uni.login({
				provider: 'weixin',
				  success: function (res) {
				    // console.log(res);
						uni.request({
							url:'https://jiafeng.boyaokj.cn/api/wechat/login',
							method:'GET',
							data:{
								code:res.code
							},
							success(res) {
								// console.log(res)
								_this.user_id = res.data.data.user_id
							}
						})
				  }
			})
			// 获取banner图
			uni.request({
				url:'https://jiafeng.boyaokj.cn/api/index/banner',
				method:'GET',
				success(res) {
					console.log(res.data.data)
					_this.banner = res.data.data
				}
			})
			// 获取新闻列表
			uni.request({
				url:'https://jiafeng.boyaokj.cn/api/news/list',
				method:'GET',
				data:{
					page:this.page,
					limit:this.limit
				},
				success(res) {
					// console.log(res.data.data)
					_this.newlist = res.data.data
				}
			})
			// 获取公告列表
			uni.request({
				url:'https://jiafeng.boyaokj.cn/api/notice/list',
				method:'GET',
				data:{
					page:this.page,
					limit:this.limit
				},
				success(res) {
					// console.log(res.data.data)
					_this.gonggao = res.data.data
				}
			})
		},
		onShow() {
			// this.rz = uni.getStorageSync('userInfo').renzheng_status,
			let that = this
			uni.request({
				url:'https://jiafeng.boyaokj.cn/api/wechat/getUserinfo',
				method:'GET',
				data:{
					user_id:uni.getStorageSync('userInfo').user_id
				},
				success(red) {
					// console.log(red.data.data)
					uni.setStorage({
						key:'userInfo',
						data:red.data.data,
					})
					that.rz = uni.getStorageSync('userInfo').renzheng_status
				}
			})
		},
		// onHide(){
		// 	if(uni.getStorageSync('userInfo').user_id == null){
		// 		this.xian = !this.xian
		// 	}
		// },
		methods:{
			fan(){
				uni.reLaunch({
					url:'../index/index'
				})
				// this.xian = !this.xian
				// uni.switchTab({
					
				// })
			},
			// 获取选择的地区
			handleGetRegion(region){
				let that = this
					this.city = region[1].name
					this.code = region[1].code
					// console.log(region[1].code)
			},
			ggdet(item){
				if(uni.getStorageSync('userInfo').user_id == null){
					this.xian = !this.xian
				}else{
					uni.navigateTo({
						url:'./gonggaodet?id='+item.id
					})
				}
				
			},
			newdet(item){
				if(uni.getStorageSync('userInfo').user_id == null){
					this.xian = !this.xian
				}else{
					uni.navigateTo({
						url:'./newdet?id='+item.id
					})
				}
				
			},
			getUserInfo(){
				let that = this
				 uni.getUserProfile({
						 desc:'Wexin',     // 这个参数是必须的
						 success:res=>{
								 // console.log(res)
								 let data = JSON.parse(res.rawData)
								 uni.request({
								 	url:'https://jiafeng.boyaokj.cn/api/wechat/setUserinfo',
									method:'GET',
									data:{
										nickname:data.nickName,
										avater:data.avatarUrl,
										country:data.country,
										gender:data.gender,
										province:data.province,
										city:data.city,
										user_id:that.user_id,
									},
									success(red) {
										// console.log(red.data.data)
										uni.setStorage({
											key:'userInfo',
											data:red.data.data
										})
									}
								 })
						 }
				 })
				},
				getPhoneNumber(e) {
								 let that = this 
									uni.login({
										provider: 'weixin',
										success(res) {
											// console.log(res)
											uni.request({
												url:'https://jiafeng.boyaokj.cn/api/wechat/setMobile',
												method:'GET',
												data:{
													user_id:uni.getStorageSync('userInfo').user_id,
													code:res.code,
													iv:e.detail.iv,
													encrypteddata:e.detail.encryptedData
												},
												success(res) {
													that.xian = !that.xian
													// console.log(res)
													// uni.setStorage({
													// 	key:'userInfo',
													// 	data:res.data.data
													// })
													uni.request({
														url:'https://jiafeng.boyaokj.cn/api/wechat/getUserinfo',
														method:'GET',
														data:{
															user_id:uni.getStorageSync('userInfo').user_id
														},
														success(red) {
															// console.log(red.data.data)
															uni.setStorage({
																key:'userInfo',
																data:red.data.data,
															})
														}
													})
												}
											})
										}
									})
				           },
			tourl(item){
				if(uni.getStorageSync('userInfo').user_id == null){
					this.xian = !this.xian
				}else{
					uni.navigateTo({
						url:item.tourl
					})
				}
				
			}
		}
	}
</script>

